def Date=new Date()+60
def NxtDay= startDate+1
def departureDay=startDate+60
def departDay=startDate+60
def arrivalDay=startDate+60
//log.info (startDate.format("yyyy-MM-dd'T'HH:mm:ss'z'"))
def val=Date.format("yyyy-MM-dd")
def val1=NxtDay.format("yyyy-MM-dd'T'23:00")
def val2=departureDay.format("yyyy-MM-dd'T'HH:mm:ss")
def val3=arrivalDay.format("yyyy-MM-dd'T'HH:mm:ss")
def val4=departDay.format("MM-dd")
testRunner.testCase.setPropertyValue( "TicketTimeLimit", val1 )
//DepartureDateTime
testRunner.testCase.setPropertyValue( "DeptDate", val2 )
//ArrivalDateTime
testRunner.testCase.setPropertyValue( "ArrivalDate", val3 )
testRunner.testCase.setPropertyValue( "Departuredate", val )
testRunner.testCase.setPropertyValue( "Departdate", val4 )

